(103, 1, '2020-05-08 19:30:42', '2020-05-08 12:30:42', '{\n    \"sidebars_widgets[sidebar-1]\": {\n        \"value\": [\n            \"search-4\",\n            \"text-5\"\n        ],\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2020-05-08 12:27:10\"\n    },\n    \"widget_pis_posts_in_sidebar[3]\": {\n        \"value\": [],\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2020-05-08 12:26:10\"\n    },\n    \"widget_text[5]\": {\n        \"value\": {\n            \"encoded_serialized_instance\": \"\",\n            \"title\": \"Th\\u00f4ng Tin C\\u00e1 Nh\\u00e2n\",\n            \"is_widget_customizer_js_value\": true,\n            \"instance_hash_key\": \"bf8b9fbbff75853158f2533e619e7e01\"\n        },\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2020-05-08 12:26:10\"\n    },\n    \"widget_text[6]\": {\n        \"value\": {\n            \"encoded_serialized_instance\": \"\",\n            \"title\": \"\",\n            \"is_widget_customizer_js_value\": true,\n            \"instance_hash_key\": \"d3d2a11666f18f8d47d7d0da77d5c65d\"\n        },\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2020-05-08 12:27:10\"\n    },\n    \"widget_search[4]\": {\n        \"value\": {\n            \"encoded_serialized_instance\": \"\",\n            \"title\": \"T\\u00ecm ki\\u1ebfm\",\n            \"is_widget_customizer_js_value\": true,\n            \"instance_hash_key\": \"05c026f8d12bc7dbbd9506d21f95177d\"\n        },\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2020-05-08 12:27:10\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', 'f9b4d48d-d024-42dc-a824-b064037bf8b6', '', '', '2020-05-08 19:30:42', '2020-05-08 12:30:42', '', 0, 'http://localhost/wordpress/?p=103', 0, 'customize_changeset', '', 0),
